size_t
sys_fork(void)
{
	return fork();
}

size_t
sys__exit(void)
{
	int status;
	PROPOGATE_ERR(argint(0, &status));

	exit(status);
	return 0; // not reached
}

size_t
sys_waitpid(void)
{
	pid_t pid;
	int *status;
	int options;
	PROPOGATE_ERR(argpid_t(0, &pid));
	PROPOGATE_ERR(argptr(1, (char **)&status, sizeof(*status)));
	PROPOGATE_ERR(argint(2, &options));

	if (options != 0 && options != WNOHANG && options != WCONTINUED &&
	    options != WSTOPPED) {
		return -EINVAL;
	}
	if (options != 0) {
		return -ENOSYS;
	}
	return waitpid(pid, status, options);
}

size_t
sys_kill(void)
{
	pid_t pid;
	int signal;

	PROPOGATE_ERR(argpid_t(0, &pid));
	PROPOGATE_ERR(argint(1, &signal));

	return kill(pid, signal);
}

size_t
sys_getpid(void)
{
	return myproc()->pid;
}

size_t
sys_getppid(void)
{
	struct proc *proc = myproc();
	if (proc != NULL && proc->parent != NULL) {
		return proc->parent->pid;
	} else {
		// If parent has been killed, use init (PID 1).
		return 1;
	}
}

size_t
sys_sbrk(void)
{
	uintptr_t addr;
	intptr_t n;

	PROPOGATE_ERR(argintptr_t(0, &n));

	addr = myproc()->sz;
	PROPOGATE_ERR(growproc(n));
	return addr;
}

size_t
sys_alarm(void)
{
	unsigned int n;
	time_t ticks0;

	PROPOGATE_ERR(argunsigned_int(0, &n));

	// Convert seconds to milliseconds.
	n *= 1000;
	acquire(&tickslock);
	ticks0 = ticks;
	while (ticks - ticks0 < n) {
		if (myproc()->killed) {
			release(&tickslock);
			return -ESRCH;
		}
		sleep(&ticks, &tickslock);
	}
	kill(myproc()->pid, SIGALRM);
	release(&tickslock);
	return 0;
}

// return how many clock tick interrupts have occurred
// since start.
size_t
sys_uptime(void)
{
	time_t xticks;

	xticks = ticks;
	return xticks;
}

size_t
sys_nanosleep(void)
{
	struct timespec *duration;
	struct timespec *rem;

	PROPOGATE_ERR(argptr(0, (char **)&duration, sizeof(*duration)));
	PROPOGATE_ERR(argptr(1, (char **)&rem, sizeof(*rem)));

	if (duration == NULL) {
		return -EFAULT;
	}
	long n = duration->tv_nsec;
	if (n < 0 || n > 9999999999) {
		return -EINVAL;
	}

	// We just validated above that n is non-negative, so this is
	// a safe conversion.
	time_t nseconds = (time_t)n + duration->tv_sec * NSEC_PER_SEC;
	hpet_timer_set_ns(hpet_get_timer_n(0), nseconds);
	hpet_waiting = true;

	acquire(&hpet_lock);
	sleep(&hpet_waiting, &hpet_lock);
	if (myproc()->killed) {
		release(&hpet_lock);
		return -ESRCH;
	}
	release(&hpet_lock);
	return 0;
}

size_t
sys_clock_gettime(void)
{
	clockid_t clockid;
	struct timespec *tp;
	PROPOGATE_ERR(argclockid_t(0, &clockid));
	PROPOGATE_ERR(argptr(1, (char **)&tp, sizeof(*tp)));
	if (tp != NULL && (uintptr_t)tp >= (uintptr_t)__kernel_begin) {
		return -EFAULT;
	}
	if (clockid != CLOCK_MONOTONIC) {
		return -ENOSYS;
	}
	if (tp != NULL) {
		*tp = (struct timespec){ rtc_now(), 0 };
	}
	return 0;
}

static void __attribute__((noreturn))
poweroff(void)
{
	// get rid of init
	kill(0, SIGKILL);
	kill(1, SIGKILL);
	// shut down qemu through magic acpi numbers
	outw(0xB004, 0x0 | 0x2000);
	outw(0x604, 0x0 | 0x2000);
	// get rid of "noreturn" warning
	panic("Unreachable.");
}
size_t
sys_reboot(void)
{
	int cmd;
	PROPOGATE_ERR(argint(0, &cmd));

	switch (cmd) {
	case RB_POWER_OFF:
		kill(1, SIGKILL);
		cprintf("Shutting down.\n");
		poweroff();
		/* unreachable */
		return 0;
		break;
	case RB_HALT:
		kill(1, SIGKILL);
		cprintf("System Halted.\n");
		cli();
		hlt();
		/* unreachable */
		return 0;
		break;
	default:
		return -EINVAL;
	}
}

size_t
sys_setgid(void)
{
	// cannot setgid if not root
	if (myproc()->cred.gid != 0) {
		return -EPERM;
	}
	gid_t gid;
	PROPOGATE_ERR(arggid_t(0, &gid));

	myproc()->cred.gid = gid;
	return 0;
}

