size_t
sys_setpgid(void)
{
	pid_t pid;
	pid_t pgid;
	struct proc *proc;
	PROPOGATE_ERR(argpid_t(0, &pid));
	PROPOGATE_ERR(argpid_t(1, &pgid));

	if (pid < 0 || pid >= NPROC) {
		return -EINVAL;
	}
	if (pgid < 0 || pgid >= NPROC) {
		return -EINVAL;
	}
	if (pid == 0) {
		proc = myproc();
		goto got_proc;
	}
	proc = get_process_from_pid(pid);
	if (proc == NULL) {
		return -ESRCH;
	}

got_proc:

	if (pgid == 0) {
		pgid = proc->pid;
	}

	proc->pgid = pgid;
	return 0;
}

