TARGET_OS=Relix CFLAGS="-Os -pipe -fno-pic -static -fno-builtin -ffreestanding -nostdlib -nostdinc -D__RELIX_USER__ -mmmx -m80387 -msse -mno-avx -I$(pwd)/../../../include -I$(pwd)/../../../. -fno-stack-protector -fno-lto -Wno-deprecated-declarations -fno-asynchronous-unwind-tables -fno-strict-aliasing" LDFLAGS="-Wl,--gc-sections -Wl,-z,noexecstack -Wl,-O1 -Wl,-N,-e,_start -Wl,--section-start=.text=0x1000" LIBS="$(pwd)/../../../bin/libc.a $(pwd)/../../../bin/sharedlib.a" sh ./Build.sh
